function atom(
    read,
    write,
  ) {
    const key = `atom${++keyCount}`
    const config = {
      toString: () => key,
    }
    if (typeof read === 'function') {
      config.read = read
    } else {
      config.init = read
      config.read = function (get) {
        return get(this)
      }
      config.write = function (
        this,
        get,
        set,
        arg,
      ) {
        return set(
          this,
          typeof arg === 'function'
            ? (arg)(get(this))
            : arg,
        )
      }
    }
    if (write) {
      config.write = write
    }
    return config
  }

