import { NotionRenderer } from 'react-notion-x';

const PostRenderer = ({ recordMap }: Props) => {
  return (
    <NotionRenderer
      recordMap={recordMap}
      fullPage
      disableHeader
      showTableOfContents
      previewImages={!!recordMap?.preview_images}
      components={{
        Code,
        Collection,
        Equation,
        Pdf,
        Modal,
        nextLink,
        nextImage,
      }}
    />
  );
};

